INSERT INTO "shiori_description" VALUES(101,1,'In Side Stories, you can unlock special stories to enjoy.\nEach story will become available as you clear the corresponding quests.');
INSERT INTO "shiori_description" VALUES(201,2,'This event features an exclusive character to unlock\nby clearing a specific mission.\nPlease do your best to achieve the mission requirements.');
INSERT INTO "shiori_description" VALUES(301,3,'When clearing this event''s quests and bosses, special Intimacy Episodes\nmay appear. You will earn Intimacy with the characters based on your\nresponses, and they may give you gifts at certain milestones.');
